#include <stdio.h>
#include <math.h>
#include <stdint.h>
#include <gmp.h>

void fermat(int p) {
	int a = 2;
	if (p % 2 == 0) {
		p = p + 1;
		fermat(p);
	}
	
	else {
		if (((int) pow(a, p - 1) % p) == 1) {
			printf("%d a prime \n", p);
		}
		else {
			fermat(p + 2);
		}
	}
}

void fermat_gmp(mpz_t p) {
	mpz_t rop;
	mpz_t a;
	mpz_t p_min;
	mpz_inits(a, rop, p_min, NULL);
	mpz_set_ui(a, 2);
	mpz_sub_ui(p_min, p, 1);

	if (mpz_odd_p(p) == 0) {
		mpz_add_ui(p, p, 1);
		fermat_gmp(p);
	}

	else {
		mpz_powm(rop, a, p_min, p);
		if (mpz_cmp_ui(rop, 1) == 0) {
			gmp_printf("%Zd is prime \n", p);
		}
		else {
			mpz_add_ui(p, p, 2);
			fermat_gmp(p);
		}
		
	}
}

int main() {
	printf("using fermat algo...\n");
	mpz_t p;
	mpz_init(p);
	mpz_set_str(p, "", 10);
	fermat_gmp(p);


	
	printf("using gmp algo...\n");
	mpz_t rop, next;
	mpz_inits(rop, next, NULL);
	mpz_set_str(next, "", 10);
	mpz_nextprime(rop, next);
	gmp_printf("%Zd is prime\n", rop);
return 0;
}
